mapper.select:

	select 
		bno, title, content, writer, regdate, updatedate
	from
	(
		select 
			/*+index_desc(tbl_board pk_board)*/ rownum rn, bno, title, content, writer, regdate, updatedate
		from 
			tbl_board
		where 
			
		

			rownum <= #{pageNum} * #{amount}
	)
	where 
		rn > (#{pageNum}-1) * #{amount}
